mapper.select:
SELECT t.LINE_ID as lineId, t.HEADLINE as headline, t.DATE_LINE as dateLine FROM ( ( SELECT DISTINCT he.LINE_ID, he.HEADLINE, he.DATE_LINE FROM `hk_iis_news_headline` he
right join `hk_iis_news_cate_ref` ca ON he.LINE_ID = ca.LINE_ID and ca.CATEGORY_ID in
#{item}
right join `hk_iis_news_cate_ref` ca ON he.LINE_ID = ca.LINE_ID and ca.CATEGORY_ID not in
#{item}
right JOIN `hk_iis_news_security_ref` se ON se.LINE_ID = he.LINE_ID AND se.SEC_CODE = #{code,jdbcType=VARCHAR} WHERE he.ATTACHMENT_NUM != 0 AND he.LANGUAGE_ID = 2 ) UNION ALL ( SELECT DISTINCT he.LINE_ID, he.HEADLINE, he.DATE_LINE FROM `hk_iis_news_headline_historic` he
right join `hk_iis_news_cate_ref_historic` ca ON he.LINE_ID = ca.LINE_ID and ca.CATEGORY_ID in
#{item}
right join `hk_iis_news_cate_ref_historic` ca ON he.LINE_ID = ca.LINE_ID and ca.CATEGORY_ID not in
#{item}
right JOIN `hk_iis_news_security_ref_historic` se ON se.LINE_ID = he.LINE_ID AND se.SEC_CODE = #{code,jdbcType=VARCHAR} WHERE he.ATTACHMENT_NUM != 0 AND he.LANGUAGE_ID = 2 ) ) t ORDER BY t.DATE_LINE DESC
